def count_xmas(matrix):
    rows = len(matrix)
    cols = len(matrix[0])
    word = "XMAS"
    word_len = len(word)
    directions = [
        (0, 1),   # Horizontal right
        (0, -1),  # Horizontal left
        (1, 0),   # Vertical down
        (-1, 0),  # Vertical up
        (1, 1),   # Diagonal down-right
        (-1, -1), # Diagonal up-left
        (1, -1),  # Diagonal down-left
        (-1, 1)   # Diagonal up-right
    ]

    def is_valid(x, y):
        """Check if (x, y) is within the grid bounds."""
        return 0 <= x < rows and 0 <= y < cols

    def match_word(x, y, dx, dy):
        """Check if 'XMAS' appears starting at (x, y) in direction (dx, dy)."""
        for i in range(word_len):
            nx, ny = x + i * dx, y + i * dy
            if not is_valid(nx, ny) or matrix[nx][ny] != word[i]:
                return False
        return True

    count = 0
    for x in range(rows):
        for y in range(cols):
            for dx, dy in directions:
                if match_word(x, y, dx, dy):
                    count += 1
    return count


matrix = text_to_matrix(input_text)

print(count_xmas(matrix))